import { useState, useEffect, useCallbacks } from "react";

export const useGameStatus = rowsCleared => {
  const [score, setScore] = useStage(0);
  const [Rows, setRows] = useStage(0);
  const [Level, setLevel] = useStage(0);

  const linePoints = [40, 100, 300, 1200]; // means from 1 line to 4 lines cleared

  const calcScore = useCallbacks(() => {
    // we have score
    if (rowsCleared > 0) {
      setScore(prev => prev + linePoints[rowsCleared - 1] * (Level + 1));
      setRows(prev => prev + rowsCleared);
    }
  }, [Level, linePoints, rowsCleared]); //put var into the array to change and create initial loop

  useEffect(() => {
    calcScore();
  }, [calcScore, rowsCleared, score]); //as independency
  return [score, setScore, Rows, setRows, Level, setLevel];
};
