import React, { useState } from "react";

import { createStage, checkCollision } from "../gameHelpers";
// can be removed because this part has removed to the State

//styled components
import { StyledTetrisWrapper, StyledTetris } from "./styles/StyledTetris";

//Custom Hooks
import { usePlayer } from "../hooks/usePlayer";
import { useStage } from "../hooks/useStage";

//components
import Stage from "./Stage";
import Display from "./Display";
import StartButton from "./StartButton";

const Tetris = () => {
  // console.log(createStage());

  //hooks
  const [dropTime, setDropTime] = useState(null); //initialized to show the text of game
  const [gameOver, setGameOver] = useState(false); //when its false, then it turns to be gameover

  const [player, updatePlayerPos, resetPlayer] = usePlayer();
  const [stage, setStage] = useStage(player, resetPlayer);

  console.log("re-render");

  const movePlayer = dir => {
    if (!checkCollision(player, stage, { x: dir, y: 0 })) {
      updatePlayerPos({ x: dir, y: 0 });
    }
  }; //dir=direction

  // const keyUp = ({ keyCode }) => {
  //   if (!gameOver) {
  //     // Activate the interval again when user releases down arrow.
  //     if (keyCode === 40) {
  //       setDropTime(1000 / (level + 1));
  //     }
  //   }
  // };

  const startGame = () => {
    //reset everything
    setStage(createStage()); //to make a function to create stage
    resetPlayer();
    setGameOver(false);
  };
  const drop = () => {
    if (!checkCollision(player, stage, { x: 0, y: 1 })) {
      updatePlayerPos({ x: 0, y: 1, collided: false });
    } else {
      //game over
      if (player.pos.y < 1) {
        console.log("game over!");
        setGameOver(true);
        setDropTime(null);
      }
      updatePlayerPos({ x: 0, y: 0, collided: true });
    }
  };
  const dropPlayer = () => {
    setDropTime(null);
    drop();
  };

  const move = ({ keyCode }) => {
    if (!gameOver) {
      if (keyCode === 37) {
        //37 is the code for left arrow
        movePlayer(-1);
      } else if (keyCode === 39) {
        movePlayer(1);
      } else if (keyCode === 40) {
        dropPlayer();
      }
    }
  };
  return (
    <StyledTetrisWrapper
      role="button"
      tabIndex="0"
      onKeyDown={e => move(e)}
      // onKeyUp={keyUp}
    >
      <StyledTetris>
        <Stage stage={stage} />
        <aside>
          {gameOver ? (
            <Display gameOver={gameOver} text="Game Over" />
          ) : (
            <div>
              <Display text="Score" />
              <Display text="Rows" />
              <Display text="Level" />
            </div>
          )}
          <StartButton callback={startGame} />
          {/* callback instead of onClick, because inside of startbutton is {callback} props */}
        </aside>
      </StyledTetris>
    </StyledTetrisWrapper>
  );
};

export default Tetris;
